import tkinter as tkfrom tkinter import messageboxfrom math import gcd# 计算 d 的函数def compute_d(p, q, e):    phi_n = (p - 1) * (q - 1)        # 计算 e 在 φ(n) 下的模逆    d, k = 1, 1    while (e * d) % phi_n != 1:        d += 1    return d, k# 按钮点击事件函数def on_calculate():    try:        p = int(entry_p.get())        q = int(entry_q.get())        e = int(entry_e.get())        n = p * q        # 检查 e 和 φ(n) 是否互质        phi_n = (p - 1) * (q - 1)        if gcd(e, phi_n) != 1:            messagebox.showerror("错误", "e 必须与 φ(n) 互质")            return        d, k = compute_d(p, q, e)        messagebox.showinfo("结果", f"计算得到的私钥 d 为: {d}\n模数 n 为: {n}\n找到的 k 值为: {k}")    except ValueError:        messagebox.showerror("错误", "请输入有效的数字")# 创建主窗口root = tk.Tk()root.title("RSA 私钥计算器")# 输入字段label_p = tk.Label(root, text="质数 p:")label_p.grid(row=0, column=0)entry_p = tk.Entry(root)entry_p.grid(row=0, column=1)label_q = tk.Label(root, text="质数 q:")label_q.grid(row=1, column=0)entry_q = tk.Entry(root)entry_q.grid(row=1, column=1)label_e = tk.Label(root, text="公钥指数 e:")label_e.grid(row=2, column=0)entry_e = tk.Entry(root)entry_e.grid(row=2, column=1)button_calculate = tk.Button(root, text="计算私钥 d", command=on_calculate)button_calculate.grid(row=3, columnspan=2)# 启动应用程序root.mainloop()